#include <iostream>

// Function to calculate the nth number in the sequence
int calculateNthNumber(int n) {
    if (n <= 0) {
        return 0;
    } else if (n == 1) {
        return 0;
    } else if (n == 2) {
        return -1;
    } else if (n == 3) {
        return -1;
    } else {
        int a = 0, b = -1, c = -1;
        for (int i = 4; i <= n; i++) {
            int temp = a + b + c;
            c = b;
            b = a;
            a = temp;
        }
        return a;
    }
}

int main() {
    int n;
    std::cout << "Enter a positive integer: ";
    std::cin >> n;
    if (n <= 0) {
        std::cout << "Invalid input. Please enter a positive integer." << std::endl;
    } else {
        int result = calculateNthNumber(n);
        std::cout << "The " << n << "th number in the sequence is: " << result << std::endl;
    }
    return 0;
}
```

